import React from "react"
import Navigation from "./components/Navigation"
import "./css/App.scss"
import ToDoContainer from "./components/ToDoContainer"
import ToDonesContainer from "./components/ToDonesContainer"

// User interface (UI) unit (component)
class App extends React.Component{

   state={
     todoItems:[
      {id:1, text:"breakfast", done: false},
      {id:2, text:"get into shower", done: false},
      {id:3, text:"start lesson", done: false},
      {id:4, text:"short break", done: false},
      {id:1, text:"Coding practice",done:true},
      {id:2, text:"Exercises",done:true},
      {id:3, text:"research on project",done:true},
      {id:4, text:"work on project",done:true},
     ]
   }

   addItem=(value)=>{
    console.log(this,"this is from App");
      let item ={id: this.state.todoItems.length, text: value, done:false}
      /* this.setState({
        todoItems:[ ...this.state.todoItems,item]
      }) */

      // second way
      let copystate=[...this.state.todoItems]
      copystate.push(item)
      this.setState({
        todoItems:copystate
      })

   }

   updateItem=()=>{
     let updateItems = this.state.todoItems.map(item)
   }

   // data is passed from parent to child through props
  render(){

    let toDos=this.state.todoItems.filter(item=>!item.done)
    let toDones=this.state.todoItems.filter(item=>item.done)
    return (
    <div className="app">
        <Navigation/>
        <ToDoContainer toDos={toDos} addItem={this.addItem} />
        <ToDonesContainer toDones={toDones} />
    </div>
  );
}
  }


export default App;
