    let updated
}

  render(){

    let toDos=this.state.todoItems.filter(item=>!item.done)
    let toDones=this.state.todoItems.filter(item=>item.done)
    return (
      <BrowserRouter>

    <div className="app">
        <Navigation/>
        <Switch>
        <Route exact path= "/">
        <ToDoContainer toDos={toDos} addItem={this.addItem} updateItem={this.updateItem} />
        <ToDonesContainer toDones={toDones} updateItem={this.updateItem}  />
        </Route>
        <Route path="/about" component={About}/> 
        <Route component={NotFound} />  {/* default case */}
        </Switch>
    </div>

    </BrowserRouter>
  );
}
  }

export default App;
