import React from "react"
import Navigation from "./components/Navigation"
import "./css/App.scss"
import ToDoContainer from "./components/ToDoContainer"
import ToDonesContainer from "./components/ToDonesContainer"
import {BrowserRouter,Route,Switch} from "react-router-dom"
import About from "./components/About"

// User interface (UI) unit (component)
class App extends React.Component{

   state={
     todoItems:[
      {id:0, text:"breakfast", done: false},
      {id:1, text:"get into shower", done: false},
      {id:2, text:"start lesson", done: false},
      {id:3, text:"short break", done: false},
      {id:4, text:"Coding practice",done:true},
      {id:5, text:"Exercises",done:true},
      {id:6, text:"research on project",done:true},
      {id:7, text:"work on project",done:true},
     ]
   }

   addItem=(value)=>{
    console.log(this,"this is from App");
      let item ={id: this.state.todoItems.length, text: value, done:false}
      /* this.setState({
        todoItems:[ ...this.state.todoItems,item]
      }) */

      // second way
      let copystate=[...this.state.todoItems]
      copystate.push(item)
      this.setState({
        todoItems:copystate
      })

   }

   updateItem=(id)=>{
     let updateItems = this.state.todoItems.map(item=>{
       if (item.id===id) {
         item.done =!item.done
         return item
       } else{
         return item
       }
     })
     this.setState({
       todoItems:updateItems
     })
   }

   // data is passed from parent to child through props
  render(){

    let toDos=this.state.todoItems.filter(item=>!item.done)
    let toDones=this.state.todoItems.filter(item=>item.done)
    return (
      <BrowserRouter>

    <div className="app">
        <Navigation/>
        <Switch>
        <Route path= "/">
        <ToDoContainer toDos={toDos} addItem={this.addItem} updateItem={this.updateItem} />
        <ToDonesContainer toDones={toDones} updateItem={this.updateItem}  />
        </Route>
        <Route path="/about" component={About}/> 
        <Route component={NotFound} />  {/* default case */}
        </Switch>
    </div>

    </BrowserRouter>
  );
}
  }

export default App;
